		$page = preg_replace('~<amp-img alt=\'\' src=\'[^\']+\' srcset=\'[^\']+\' class=\'[^\']+\' height=\'\d+\' width=\'\d+\' />[\s\t]*~i', '', $page);


		$this->transcode_head($page, $amp_style, $home_url, $permalink, $canonical_url, $viewport_width);
	}
}
